        bezierPath.addCurveToPoint(CGPointMake(layer_1.minX + 0.51921 * layer_1.width, layer_1.minY + 0.67078 * layer_1.height), controlPoint1: CGPointMake(layer_1.minX + 0.51354 * layer_1.width, layer_1.minY + 0.68387 * layer_1.height), controlPoint2: CGPointMake(layer_1.minX + 0.51647 * layer_1.width, layer_1.minY + 0.67723 * layer_1.height))
        bezierPath.addCurveToPoint(CGPointMake(layer_1.minX + 0.51673 * layer_1.width, layer_1.minY + 0.66267 * layer_1.height), controlPoint1: CGPointMake(layer_1.minX + 0.52039 * layer_1.width, layer_1.minY + 0.66801 * layer_1.height), controlPoint2: CGPointMake(layer_1.minX + 0.51948 * layer_1.width, layer_1.minY + 0.66505 * layer_1.height))
