import UIKit

public class PalmOilGlyphs : NSObject {

    //// Drawing Methods

    public class func drawBarcodeBar(#frame: CGRect, borderColor: UIColor) {
        //// General Declarations
        let context = UIGraphicsGetCurrentContext()

        //// Rectangle Drawing
        let rectanglePath = UIBezierPath(roundedRect: CGRectMake(frame.minX, frame.minY, frame.width, frame.height), cornerRadius: 4)
        borderColor.setStroke()
        rectanglePath.lineWidth = 2
        CGContextSaveGState(context)
        CGContextSetLineDash(context, 0, [2, 7], 2)
        rectanglePath.stroke()
        CGContextRestoreGState(context)
    }

}

